import React, { useState } from 'react';
import PoultryLogo from '../Asset/PoultryLogo.jpg'
import Cart from '../Asset/Cart.png'
import './Navbar.css';
import { Link } from 'react-router-dom';

function Navbar(){
    const[activemenu, setActiveMenu] = useState("shop");
    return(
        <div className="navbar">
            <div className="navbar_logo">
                <img className='logo' src={PoultryLogo} alt="" />
                <p className="logo_name">Commerce</p>

            </div>
            <div className="navbar_menu">  
                 <li className="catagory_menu" onClick={()=>setActiveMenu("shop")}> <Link to='/'>Shop</Link>  {activemenu ==="shop"? <hr/> : <></>} </li>
                <li className="catagory_menu"  onClick={()=>setActiveMenu("meat")}> <Link to='/meat'>Meat</Link> {activemenu ==="meat"? <hr/> : <></>} </li>
                <li className="catagory_menu"  onClick={()=>setActiveMenu("eggs")}> <Link to='/eggs'>Eggs</Link> {activemenu ==="eggs"? <hr/> : <></>} </li>
                <li className="catagory_menu"  onClick={()=>setActiveMenu("tools")}> <Link to='/tools'>Tools</Link>  {activemenu ==="tools"? <hr/> : <></>} </li>
               
            </div>

            <div className="login_cart">
                <Link to ='/login'><button className="login">Login</button></Link>
                <Link to ='/cart'><img className='cart' src={Cart} alt="Cart"/></Link>
                <div className="cart_count">0</div>
            
            </div>
        </div>
    );
}
export default Navbar;
