import React, { useEffect, useState } from 'react'
import './CSS/Loginsignu.css'

const LoginSignUp = () => {
    const [fName, setfName] = useState("");
    const [lName, setlName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const [msg, setMsg] = useState("");

    useEffect(()=> {
      setTimeout(function(){
        setMsg("");
      }, 15000);
    }, [msg]);
    const handleInputChange = (e, type)=>{
      switch(type){
        case "fname":
          setError("");
          setfName(e.target.value);
          if(e.target.value === ""){
            setError("First name has left black")
          }

          break;

        case "lname":
            setError("");
            setlName(e.target.value);
            if(e.target.value === ""){
              setError("Last name has left black")
            }
  
            break;
        
        case "email":
              setError("");
              setEmail(e.target.value);
              if(e.target.value === ""){
                setError("Email has left black")
              }
    
              break;

        case "password":
                setError("");
                setPassword(e.target.value);
                if(e.target.value === ""){
                  setError("Password has left black")
                }
      
                break;

        default: 
      }
    }

    function handleSubmit(){
      if(fName !== "" && lName !== "" && email !== "" && password !== ""){
        var url = "http://localhost/poultry/signUp.php";
        var headers = {
           Accept: "application/json",
          "Content-Type": "application/json"
        };
        var data = {
          fName: fName,
          lName: lName,
          email: email,
          password: password
        }

        fetch(url, {
          method: "POST",
          headers: headers,
          body: JSON.stringify(data)
        }).then((response) => response.json())
          .then((response) => {
            setMsg(response[0].result)
          }).catch((err) =>{
            setError(err.message);
            console.error(err)
          });
          setfName("");
          setlName("");
          setEmail("");
          setPassword("");
      }

      else{
        setError("All fields are required");
      }
    }



  return (
    <div className='loginsignup'>
      <div className="loginsignup_container">
        <h1>Sign Up</h1>
        <p>
          {
            msg !== "" ?
            <span className="succesmsg">{msg}</span> :
            <span className="errormsg">{error}</span>            
          }
        </p>
        <div className="loginsignup_fields">
          <input type="text" name='fName' placeholder='First name' value={fName} 
            onChange={(e) => handleInputChange(e, "fname")}/>
          <input type="text" name='lName' placeholder='Last name' value={lName}
             onChange={(e) => handleInputChange(e, "lname")}/>
          <input type="email" name='email' placeholder='Email Address' value={email}
             onChange={(e) => handleInputChange(e, "email")}/>
          <input type="password" name='password' placeholder='password' value={password}
             onChange={(e) => handleInputChange(e, "password")}/>
        </div>
        <button onClick={handleSubmit}>Continue</button>
        <p className="loginsignup_login">
          Already have an Account?  <span>Login here</span>
        </p>
        <div className="loginsignup_agree">
          <input type="checkbox" id='' name='' />
          <p>By continuing, I agree to the terms of use privacy policy.</p>
        </div>
      </div>
    </div>
  )
}

export default LoginSignUp
