import React, { useEffect, useState } from 'react'
import './CSS/Loginsignu.css'
import { Link, useNavigate } from 'react-router-dom';

const LoginSignUp = () => {
    const [fullName, setFullName] = useState("");
    const [fName, setfName] = useState("");
    const [lName, setlName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const [msg, setMsg] = useState("");
    const navigate = useNavigate();

    useEffect(()=> {
      setTimeout(function(){
        setMsg("");
      }, 15000);
    }, [msg]);
    
    const handleInputChange = (e, type)=>{
      switch(type){
        case "fullName":
          setError("");
          setFullName(e.target.value);
          if(e.target.value === ""){
            setError("Full name has left black")
          }
  
            break;
        
        case "email":
              setError("");
              setEmail(e.target.value);
              if(e.target.value === ""){
                setError("Email has left black")
              }
    
              break;

        case "password":
                setError("");
                setPassword(e.target.value);
                if(e.target.value === ""){
                  setError("Password has left black")
                }
      
                break;

        default: 
      }
    }


    const handleSubmit = async () => {

      if (fName && lName && email && password) {
          const url = "http://localhost/poultry/signUp.php";
          const headers = {
              Accept: "application/json",
              "Content-Type": "application/json",
          };
          const data = {
              fName,
              lName,
              email,
              password
          };

          try {
              const response = await fetch(url, {
                  method: "POST",
                  headers: headers,
                  body: JSON.stringify(data)
              });

              const result = await response.json();
              if (response.ok) {
                  setMsg(result[0].result); // Adjust based on your backend response
                  // Reset fields only on success
                  setfName("");
                  setlName("");
                  setEmail("");
                  setPassword("");
                  navigate('/'); // Redirect to the Meat category page
              } else {
                  setError(result[0].result || "An error occurred during signup.");
              }
          } catch (err) {
              setError("Network error: " + err.message);
              console.error(err);
          }
      } else {
          setError("All fields are required");
      }
  };

  return (
    <div className='loginsignup'>
      <div className="loginsignup_container">
        <h1>Sign Up</h1>
        <p>
          {
            msg !== "" ?
            <span className="succesmsg">{msg}</span> :
            <span className="errormsg">{error}</span>            
          }
        </p>
        <div className="loginsignup_fields">
          <input type="text" name='fName' placeholder='Full name' value={fullName} 
            onChange={(e) => handleInputChange(e, "fullName")}/>
          <input type="email" name='email' placeholder='Email Address' value={email}
             onChange={(e) => handleInputChange(e, "email")}/>
          <input type="password" name='password' placeholder='password' value={password}
             onChange={(e) => handleInputChange(e, "password")}/>
        </div>
        <button onClick={handleSubmit}>Continue</button>
        <p className="loginsignup_login">
          Already have an Account?  <Link to= "/login"><span>Login here</span></Link> 
        </p>
        <div className="loginsignup_agree">
          <input type="checkbox" id='' name='' />
          <p>By continuing, I agree to the terms of use privacy policy.</p>
        </div>
      </div>
    </div>
  )
}

export default LoginSignUp
